<script setup>
import { computed, onMounted, watch } from 'vue'

const props = defineProps(['dark'])

const isDark = computed(() => {
  return props.dark
})

onMounted(() => {
  switchThemeColor()
})

const switchThemeColor = () => {
  if (isDark.value) {
    document.querySelector(':root').style.setProperty('--themeColor', `#ffffff`)
    document.querySelector(':root').style.setProperty('--themeTop', `#f9fafb`)
    document.querySelector(':root').style.setProperty('--themeLeft', `#e5e7eb`)
    document.querySelector(':root').style.setProperty('--themeRight', `#9ca3af`)
    document.querySelector(':root').style.setProperty('--themeLogic', `#374151`)
    document.querySelector(':root').style.setProperty('--themeLogicBackground', `#f9fafb`)
    document.querySelector(':root').style.setProperty('--themeEnvelope', `#f9fafb`)
    document
      .querySelector(':root')
      .style.setProperty('--themeShadow', `drop-shadow(0px 2px 2px rgba(98, 98, 98, 1))`)
  } else {
    document.querySelector(':root').style.setProperty('--themeColor', `#000000`)
    document.querySelector(':root').style.setProperty('--themeTop', `#6b7280`)
    document.querySelector(':root').style.setProperty('--themeLeft', `#4b5563`)
    document.querySelector(':root').style.setProperty('--themeRight', `#1f2937`)
    document.querySelector(':root').style.setProperty('--themeLogic', `#4b5563`)
    document.querySelector(':root').style.setProperty('--themeLogicBackground', `#e5e7eb`)
    document.querySelector(':root').style.setProperty('--themeEnvelope', `#e5e7eb`)
    document
      .querySelector(':root')
      .style.setProperty('--themeShadow', `drop-shadow(0px 2px 2px rgba(0, 0, 0, 0.25))`)
  }
}

watch(isDark, () => {
  switchThemeColor()
})
</script>

